import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import ExibicaoDados from './components/ExibicaoDados';
import ImportacaoPedidos from './components/ImportacaoPedidos';
import './App.css'; // Importar o arquivo CSS

function App() {
  return (
    <div>
      <nav>
        <ul>
          <li>
            <Link to="/importacao">Importação de Pedidos</Link>
          </li>
          <li>
            <Link to="/exibicao">Exibição de Dados</Link>
          </li>
        </ul>
      </nav>

      <div className="container">
        <Routes>
          <Route path="/importacao" element={<ImportacaoPedidos />} />
          <Route path="/exibicao" element={<ExibicaoDados />} />
          <Route path="/" element={<HomePage />} />
        </Routes>
      </div>
    </div>
  );
}

// Componente para a página inicial com uma descrição amigável
function HomePage() {
  return (
    <div>
      <h1>Bem-vindo ao Sistema de Gestão de Pedidos</h1>
      <p>
        Este sistema permite a importação e visualização de pedidos. Você pode
        importar pedidos a partir de arquivos CSV ou Excel e visualizar as
        estatísticas de vendas por região e produto.
      </p>
      <h2>Funcionalidades:</h2>
      <ul>
        <li><strong>Importação de Pedidos:</strong> Importe pedidos através de arquivos CSV ou Excel.</li>
        <li><strong>Exibição de Dados:</strong> Visualize dados de vendas em gráficos e tabelas.</li>
      </ul>
      <p>Utilize a navegação acima para acessar as funcionalidades do sistema.</p>
    </div>
  );
}

export default App;
